import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import pfp_default from "../img/pfp_default.jpg";
import thumbnail_default from "../img/thumbnail_default.jpg";

const CardContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  gap: 20px;
`;

// Update the card width to fit three in a row
const Container = styled.div`
  flex: 1 1 calc(33.33% - 20px);
  margin-bottom: 45px;
  cursor: pointer;
  max-width: 440px;
`;

const Details = styled.div`
  display: flex;
  margin-top: 16px;
  gap: 12px;
`;

const ChannelImage = styled.img`
  width: 36px;
  height: 36px;
  border-radius: 50%;
`;

const Image = styled.img`
  height: 80%;
  height: 150px;
`;

const Texts = styled.div``;

const Title = styled.h1`
  font-size: 16px;
  font-weight: 500;
  color: ${({ theme }) => theme.text};
`;

const ChannelName = styled.h2`
  font-size: 14px;
  color: ${({ theme }) => theme.textSoft};
  margin: 9px 0px;
`;

const Info = styled.div`
  font-size: 14px;
  color: ${({ theme }) => theme.textSoft};
`;

const Card = () => {
  return (
    <Link to="/video/test" style={{ textDecoration: "none" }}>
      <CardContainer>
        <Container>
          <Image src={thumbnail_default} />
          <Details>
            <ChannelImage src={pfp_default} />
            <Texts>
              <Title>Mystery of Golgotha</Title>
              <ChannelName>Rudolf Steiner</ChannelName>
              <Info> 444,888 views â€¢ 4 day ago</Info>
            </Texts>
          </Details>
        </Container>
      </CardContainer>
    </Link>
  );
};

export default Card;
